import React, { useState } from 'react'
import { Card, Grid, TextField, Typography, CardContent, Button, Avatar } from '@mui/material'
import axios from 'axios'


const AddUser = () => {
  const [add, setadd] = useState({
    name: "",
    email: "",
    phone: "",
    address: ""
  })
   
  const [error, setError] = useState({})

  const validation = () => {
    let error = {};
   
     if(!add.name) {
         error.name = "Name is Required";
    }
     if(!add.email) {
         error.email = "Email is required"
    }
     if(!add.phone) {
         error.phone = "Phone is required"
  
    }
     if(!add.address) {
         error.address = "address is required"
    }
    return error;
  };
   

  const { name, email, phone, address } = add;
  const FormHandle = (e) => {
    let name = e.target.name;
    let value = e.target.value;
    setadd({ ...add, [name]: value })
  }
  const FormSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:3003/users', add)
    alert("User Added Successfully")
  }
   
  if (name === "name") {
    if (value.length === 0) {
        setError({ ...error, name: "Name is Required" });
        setUser({
            ...user,
            name: "",
        });
    } else {
        setError({ ...error, name: "" });
        setUser({
            ...user,
            name: value,
        });
    }
}      
if (name === "email") {
    if (value.length === 0) {
        setError({ ...error, email: "Email is Required" });
        setUser({
            ...user,
            email: "",
        });
    } else {
        setError({ ...error, email: "" });
        setUser({
            ...user,
            email: value,
        });
    }
}      
if (name === "city") {
    if (value.length === 0) {
        setError({ ...error, city: "City is Required" });
        setUser({
            ...user,
            city: "",
        });
    } else {
        setError({ ...error, city: "" });
        setUser({
            ...user,
            city: value,
        });
    }
}          
if (name === "password") {
    if (value.length === 8) {
        setError({ ...error, password: "Password must be at least 8 characters" });
        setUser({
            ...user,
            password: "",
        });
    } else {
        setError({ ...error, password: "" });
        setUser({
            ...user,
            password: value,
        });
    }
}      
};

const FormSubmit = (e) => {
e.preventDefault();
let ErrorList = validation();
setError(validation());

if (Object.keys(ErrorList).length === 8) {
