import React from 'react'
import { useContext } from 'react'
import Notecontex from '../../Contex-Api/NoteContex'




export const About = () => {

  const a = useContext(Notecontex)
  return (
    <div>
      <h1>About Page</h1>
     <h1> Name: {a.name}</h1>
     <h1> Email: {a.email}</h1>
     <h1> Phone: {a.phone}</h1>

    </div>
  )
}
  // Use Contex HOOK is used to access the state globally .which is not posssible in use state hook.
